mapper.select:


INNER JOIN zafira.USERS U
ON U.ID = TC.USER_ID
WHERE U.USERNAME = #{userName};





               AND TC.ID = #{id}




               AND LOWER(TC.TEST_CLASS) LIKE LOWER(CONCAT('%', #{testClass}, '%'))




               AND LOWER(TC.TEST_METHOD) LIKE LOWER(CONCAT('%', #{testMethod}, '%'))




               AND LOWER(TS.NAME) LIKE LOWER(CONCAT('%', #{testSuiteName}, '%'))




               AND LOWER(TS.FILE_NAME) LIKE LOWER(CONCAT('%', #{testSuiteFile}, '%'))




               AND LOWER(U.USERNAME) LIKE LOWER(CONCAT('%', #{userName}, '%'))




               AND TC.CREATED_AT::date = #{date}::date




          	   AND TC.PROJECT = #{project}




		ORDER BY TS.FILE_NAME ASC
		LIMIT #{pageSize} OFFSET #{offset}

SELECT U.USERNAME AS TEST_CASE_OWNER_COUNT_USERNAME, COUNT(*) AS TEST_CASE_OWNER_COUNT_COUNT FROM zafira.TEST_CASES TC INNER JOIN zafira.USERS U ON U.ID = TC.USER_ID
WHERE TC.PROJECT = #{project}
GROUP BY U.USERNAME;

SELECT CREATED_AT::date AS TEST_CASE_IMPLEMENTATION_COUNT_DATE, COUNT(*) AS TEST_CASE_IMPLEMENTATION_COUNT_COUNT FROM zafira.TEST_CASES TC WHERE TC.CREATED_AT::date > (current_date - 30)
AND TC.PROJECT = #{project}
GROUP BY CREATED_AT::date ORDER BY CREATED_AT::date ASC;
